import { createAsyncThunk } from "@reduxjs/toolkit";
import axios from "axios";
import { profileError } from "../slice/profileSlice";
import { setAlert } from "./setAlert";

export const getCurrentProfile = createAsyncThunk(
  "profile/getCurrentProfile",
  async (_, thunkAPI) => {
    try {
      const res = await axios.get("/api/profile/me");

      return res.data;
    } catch (err) {
      // thunkAPI.dispatch(clearProfile());
      thunkAPI.dispatch(
        profileError({
          msg: err.response.statusText,
          status: err.response.status,
        })
      );
    }
  }
);

// Create or update profile
export const createProfile = createAsyncThunk('profile/createProfile', async (res, thunkAPI) => {
  const { formData, navigate, edit = false } = res
  try {
    const config = {
      headers: {
        'Content-Type': 'application/json'
      }
    }

    const response = await axios.post('/api/profile', formData, config)

    thunkAPI.dispatch(setAlert(edit ? 'Profile Updated' : 'Profile Created'))

    if(!edit) {
      navigate('/dashboard')
    }

    return response.data
  } catch (err) {
    const errors = err.response.data.errors;

      if (errors) {
        errors.forEach((error) =>
          thunkAPI.dispatch(setAlert({ msg: error.msg, alertType: "danger" }))
        );
        thunkAPI.dispatch(authError());
      }
  }
})
