import { createAsyncThunk } from "@reduxjs/toolkit";
import axios from "axios";
import { postError } from "../slice/postSlice";

// Get posts
export const getPost = createAsyncThunk("post/getPost", async (_, thunkAPI) => {
  try {
    const res = await axios.get("/api/posts");
    return res.data;
  } catch (err) {
    thunkAPI.dispatch(
      postError({
        msg: err.response.statusText,
        status: err.response.status,
      })
    );
  }
});

// Add like
export const addLike = createAsyncThunk(
  "post/addLike",
  async (id, thunkAPI) => {
    try {
      if (id === "") {
        return " ";
      } else {
        const response = await axios.put(`/api/posts/like/${id}`);
        return { id, likes: response.data };
      }
    } catch (err) {
      thunkAPI.dispatch(
        postError({
          msg: err.response,
          status: err.response.status,
        })
      );
    }
  }
);

