import { createAsyncThunk } from "@reduxjs/toolkit";
import { v4 as uuid } from "uuid";
import { removeAlert } from "./removeAlert";

export const setAlert = createAsyncThunk("alert/set", (res) => {
  const id = uuid();

  return { res, id };

  setTimeout(() => removeAlert)
});
