// import { createAsyncThunk } from "@reduxjs/toolkit";
import { v4 as uuid } from "uuid";
import { useDispatch } from "react-redux";

export const setAlert = (msg, alertType) => {
  const id = uuid();
  const dispatch = useDispatch();
  return  dispatch({ msg, alertType, id });
};
