import { createAsyncThunk } from "@reduxjs/toolkit";
import { v4 as uuid } from "uuid";

export const setAlert = createAsyncThunk("alert/set", ( msg, alertype ) => {
  const id = uuid();
  return { msg, alertype, id };
});
