import { createAsyncThunk } from "@reduxjs/toolkit";
import { v4 as uuid } from "uuid";

export const setAlert = createAsyncThunk("alert/set", (result) => {
  const id = uuid();
  const { msg, alertType } = result;
  return { msg, alertType, id };
});
