import { createAsyncThunk } from "@reduxjs/toolkit";
// import { v4 as uuid } from "uuid";

export const setAlert = createAsyncThunk(
  "alert/set",
  (msg, alertType, alert) => {
    const id = 24;
    return { msg, alertType, alert, id };
  }
);
