import { createAsyncThunk } from "@reduxjs/toolkit";
import { v4 as uuid } from "uuid";

export const setAlert = createAsyncThunk("alert/set", ( alertType, msg ) => {
  const id = uuid();
  return { alertType,msg, id };
});
