import { createSlice } from "@reduxjs/toolkit";
import { setAlert } from "../thunks/setAlert";
import { fetchAlert } from "../thunks/fetchAlerts";

const alertSlice = createSlice({
  name: "alert",
  initialState: {
    data: [],
    loading: false
  },
  extraReducers(builder) {
    builder.addCase(fetchAlert.pending, (state, action) => {
      state.loading = true
    });
    builder.addCase(fetchAlert.fulfilled, (state, action) => {
      state.data = action.payload;
      state.loading = false
    });
    builder.addCase(setAlert.fulfilled, (state, action) => {
      state.data = action.payload;
    });

    // builder.addCase(fetchAlert.fulfilled, (state, action) => {
    //     state.filter(alert => alert.id !== action.payload)
    // })
  },
});

export const alertReducer = alertSlice.reducer;
