import { createSlice } from "@reduxjs/toolkit";
import { getCurrentProfile } from "../thunks/profile";

const profileSlice = createSlice({
  name: "profile",
  initialState: {
    profile: null,
    profiles: [],
    repos: [],
    loading: true,
    error: {},
  },
  reducers: {
    profileError: (state, action) => {
      state.error = action.payload;
      state.profile = null;
    },
    clearProfile: (state) => {
      state.profile = null;
      state.repos = [];
      state.loading = false;
    },
  },

  extraReducers(builder) {
    builder.addCase(getCurrentProfile.fulfilled, (state, action) => {
      state.profile = action.payload;
      state.loading = false;
    });
    builder.addCase(crea.fulfilled, (state, action) => {
      state.profile = action.payload;
      state.loading = false;
    });
  },
});

export const profileReducer = profileSlice.reducer;
export const { profileError, clearProfile } = profileSlice.actions;
