import { createAsyncThunk } from "@reduxjs/toolkit";
import axios from "axios";
import { setAlert } from "./setAlert";
import setAuthToken from "../../utils/setAuthToken";
import { authError, authSuccess } from "../slice/authSlice";
// import { removeToken } from "../slice/authSlice";

// Load User
export const loadUser = createAsyncThunk(
  "auth/loadUser",
  async (_, thunkAPI) => {
    if (localStorage.token) {
      setAuthToken(localStorage.token);
    }

    try {
      const res = await axios.get("/api/auth");
      console.log(res.data);
      thunkAPI.dispatch(authSuccess)
      return res.data;
    } catch (err) {
      thunkAPI.dispatch(authError());
    }
  }
);

// Register User
export const register = createAsyncThunk(
  "auth/register",
  async (res, thunkAPI) => {
    const { name, email, password } = res;

    const config = {
      headers: {
        "Content-Type": "application/json",
      },
    };

    const body = JSON.stringify({ name, email, password });

    try {
      const response = await axios.post("/api/users", body, config);
      thunkAPI.dispatch(setAlert({ msg: "Logged In", alertType: "success" }));
      return response.data.token;
    } catch (err) {
      const errors = err.response.data.errors;

      if (errors) {
        errors.forEach((error) =>
          thunkAPI.dispatch(setAlert({ msg: error.msg, alertType: "danger" }))
        );
        // const registerFail = createAction("REGISTER_FAIL");
        // thunkAPI.dispatch(registerFail(removeToken));
      }
    }
  }
);
